)

CREATE TABLE SGArquivos
(
    Id int primary key,
    Nome varchar(50) not null,
    Arquivo varbinary(max) not null
)

create table SGClienteFicha(
cliId int primary key identity,
cliNome varchar(50) not null,
cliTel varchar(50) not null,
cliEmail varchar(30) not null,
cliCEP varchar(30) not null,
cliAddress varchar(50) not null,
cliCidade varchar(30) not null,
cliCPF varchar(50) not null,
cliPsw varchar(56) not null
)

create table SGFichaDeReclamacao(
ficId int primary key identity,
ficCliId int not null,
ficTipo varchar(30) not null,
ficDesc varchar(300) not null,
ficArqId int,

ficDataEmicao datetime not null,

ficMotivoNAceito varchar(100),
ficFunNAceito varchar(50),

ficDataPrevista date ,
ficFuncPrevista varchar(30),
ficCausaId int,
ficCorrecao varchar(150),

ficDataConcluido date ,
ficFuncConcluido varchar(30),



constraint fk_ficCli foreign key (ficCliId) references SGClienteFicha(cliId),
constraint fk_ficCausa foreign key (ficCausaId) references SGCausas(cauId),
constraint fk_ficArqId foreign key (ficArqId) references SGArquivos(id)
)


